(292, 1, '2023-12-31 14:55:52', '2023-12-31 05:55:52', '<table class=\"mailform\">\n    <tbody>\n        <tr>\n            <th>\n                <p class=\"fname\">会社名</p>\n            </th>\n            <td>[mwform_text name=\"company_name\" class=\"f-inpt\" size=\"60\"]</td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">部署名</p>\n            </th>\n            <td>[mwform_text name=\"department\" class=\"f-inpt\" size=\"60\"]</td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">お名前</p>\n                <p class=\"fmust\">必須</p>\n            </th>\n            <td><span class=\"info\">（※漢字でご記入ください）</span>[mwform_text name=\"name\" class=\"f-inpt\" size=\"60\"]</td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">フリガナ</p>\n            </th>\n            <td><span class=\"info\">（※カタカナでご記入ください）</span>[mwform_text name=\"furigana\" class=\"f-inpt\" size=\"60\"]</td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">メールアドレス</p>\n                <p class=\"fmust\">必須</p>\n            </th>\n            <td>\n                <span class=\"info\">（※半角英数字でご記入ください）</span>[mwform_text name=\"email\" class=\"f-inpt\" size=\"60\"]\n                <p class=\"remail\">[mwform_text name=\"remail\" class=\"f-inpt\" size=\"60\"]</p>\n            </td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">郵便番号</p>\n                <p class=\"fmust\">必須</p>\n            </th>\n            <td>\n                <div class=\"upostcode\">\n                    <span class=\"info\">（※半角数字でご記入ください）</span>\n                    [mwform_text name=\"post_code\" class=\"f-inpt\" size=\"60\" placeholder=\"例）123-4567\"]\n                    <input type=\"button\" name=\"render-zipcode\" value=\"住所反映\" class=\"postcode\">\n                </div>\n            </td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">都道府県</p>\n                <p class=\"fmust\">必須</p>\n            </th>\n            <td>[mwform_text name=\"address\" class=\"f-inpt\" size=\"60\"]</td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">市区町村以下</p>\n                <p class=\"fmust\">必須</p>\n            </th>\n            <td><span class=\"info\">（※市区町村以下をご記入ください）</span>[mwform_text name=\"city\" class=\"f-inpt\" size=\"60\"]</td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">お電話番号</p>\n                <p class=\"fmust\">必須</p>\n            </th>\n            <td>[mwform_text name=\"tel\" class=\"f-inpt\" size=\"60\"]</td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">用件区分</p>\n                <p class=\"fmust\">必須</p>\n            </th>\n            <td>\n                <div class=\"flist flist-checkbox\">[mwform_checkbox name=\"classification\" class=\"f-inpt\" children=\"key1:お問い合わせ,key2:資料請求,key3:お見積もり,key4:ご注文,key5:その他\" separator=\",\"]</div>\n            </td>\n        </tr>\n        <tr>\n            <th>\n                <p class=\"fname\">お問い合わせ内容</p>\n            </th>\n            <td>\n                \n            </td>\n        </tr>\n    </tbody>\n</table>\n<div class=\"ubtn\">\n    <p class=\"ubtn-item ubtn-cancel\">\n        <span onclick=\"history.back()\">前のページへ</span>\n    </p>\n    <p class=\"ubtn-item ubtn-send\">\n        [mwform_submitButton name=\"submit\" confirm_value=\"確認画面へ進む\" submit_value=\"送信\"]\n    </p>\n</div>', 'Contact', '', 'inherit', 'closed', 'closed', '', '277-autosave-v1', '', '', '2023-12-31 14:55:52', '2023-12-31 05:55:52', '', 277, 'http://192.168.50.54/?p=292', 0, 'revision', '', 0),
(293, 1, '2023-12-31 15:37:43', '2023-12-31 06:37:43', '<section class=\"uh3\">\r\n<div class=\"uwrap\">\r\n<div class=\"uh3-tt\">\r\n<h3>エコプランへのお問い合わせはこちら</h3>\r\n<p class=\"h3-en\">contact</p>\r\n</div>\r\n<div class=\"uh3-desc\">\r\n<p class=\"utxt\">\r\n業務用エアコン・空調機・冷凍機は企業や工場にとって必要なものですが、消費電力は非常に大きくなり費用負担も増加します。エコプランがご提供している省エネ制御装置「EcoData\r\nBeONE」は、消費電力・デマンドを効果的に削減できるシステムです。これから省エネに取り組みたい企業様は、ぜひ当社までご相談ください。\r\n</p>\r\n</div>\r\n</div>\r\n</section>\r\n\r\n<section>\r\n<div class=\"uwrap\">\r\n<h4>お問い合わせ</h4>\r\n<p class=\"utxt\">\r\n業務用エアコン・空調機・冷凍機などの消費電力を削減する、省エネ制御機器「BeONE」を製造・販売するエコプランのホームページをご覧いただき、誠にありがとうございます。<br>\r\nご意見、ご質問などがございましたら、お気軽にお問い合わせください。\r\n</p>\r\n</div>\r\n</section>\r\n\r\n<section>\r\n<div class=\"uwrap\">\r\n<h4>お電話やFAXでも承ります</h4>\r\n</div>\r\n<div class=\"ucontact-box\">\r\n<p class=\"ucontact-tt\">TEL／<a href=\"tel:0669498210\" class=\"sweetlink\" onclick=\"gtag(\'event\', \'tel\', {\'event_category\': \'sp\'});\">06-6949-8210</a></p>\r\n<p class=\"ucontact-tt\">FAX／06-6949-8220</p>\r\n<p class=\"ucontact-time\">\r\n<span>営業時間／9：00～18：00</span>\r\n<br class=\"sp\">\r\n<span>定休日／土・日曜・祝日</span>\r\n</p>\r\n</div>\r\n</section>\r\n\r\n<section class=\"tbl-bg\" id=\"form_fmail\">\r\n<div class=\"uwrap\">\r\n<h4>フォームはこちら</h4>\r\n</div>\r\n<div class=\"mw_wp_form udr-fmail\">\r\n[mwform_formkey key=\"277\"]\r\n</div>\r\n</section>', 'お問い合わせフォーム', '', 'inherit', 'closed', 'closed', '', '56-revision-v1', '', '', '2023-12-31 15:37:43', '2023-12-31 06:37:43', '', 56, 'http://192.168.50.54/?p=293', 0, 'revision', '', 0),
